---------------------------------------------------------*/#define SYMB_COPYRIGHT	"\251"#define	LOCAL_APPL	0#define LOCAL_TEXT	7#define LOCAL_PREF	8#define LOCAL_WILD	9#define	LOCAL_STAR	10#define	FREF_APPL	128#define FREF_TEXT	129#define FREF_PREF	130#define ICON_APPL	128#define ICON_TEXT	129#define ICON_PREF	130/*---------------------------------------------------------------------+\|																		||	Local type definitions												||																		|\+---------------------------------------------------------------------*/type 'gREM' as 'STR ';type 'PREF' as 'STR ';/*---------------------------------------------------------------------+\|																		||	RESOURCES															||																		|\+---------------------------------------------------------------------*/////	Signature//resource 'gREM' ( 0, "Remind signature" ){	"Remind -- written by John Griswold.  "	"Copyright 1986 .. 1997, Grizzly Software "	"a division of Bear Consulting Group"};////	Version//resource 'vers' ( 1, "application_vers", purgeable ){	VERSION_RELEASE,	((VERSION_REVISION << 4) + VERSION_FIX),	VERSION_PHASE,	VERSION_INTERNAL,	verUS,	VERSION_STRING,		VERSION_STRING " " SYMB_COPYRIGHT " 1997 Grizzly Software "	"a division of Bear Consulting Group"};resource 'vers' ( 2, "file_vers", purgeable ){	VERSION_RELEASE,	((VERSION_REVISION << 4) + VERSION_FIX),	VERSION_PHASE,	VERSION_INTERNAL,	verUS,	VERSION_STRING,		VERSION_STRING};////	Preferences//resource 'PREF' ( 128, "style::label" ){	"size=12,font=Monaco"};resource 'PREF' ( 129, "style::message" ){	"size=12,font=\"New York\""};resource 'PREF' ( 130, "style::month" ){	"size=14,font=Times,bold"};resource 'STR ' ( 128, "Default Label Fonts" ){	"Monaco:Geneva:Courier:Helvetica:New York:Palatino:Chicago:Espi Sans"};resource 'STR ' ( 129, "Default Message Fonts" ){	"New York:Times:Palatino:Helvetica:Monaco:Geneva:Courier"};////	Ballon help for finder//resource 'hfdr' ( kHMHelpID, "Ballon help for remind's icon" ){	HelpMgrVersion,	hmDefaultOptions,	0,	0,	{		HMStringItem		{			"Remind\n"			"\n"			"Remind helps you remember important dates, like "			"birthdays, anniversaries, holidays, etc..\n"			"\n"			"copyright 1997, Grizzly Software\n"			"a division of Bear Consulting Group"		}	}};////	File References//resource 'FREF' ( FREF_APPL, "APPL" ) { 'APPL', LOCAL_APPL, "" };resource 'FREF' ( FREF_TEXT, "TEXT" ) { 'TEXT', LOCAL_TEXT, "" };resource 'FREF' ( FREF_PREF, "PREF" ) { 'PREF', LOCAL_PREF, "" };resource 'BNDL' ( 128 ){	'gREM',	0,	{		'FREF',		{			LOCAL_APPL, FREF_APPL,			LOCAL_TEXT, FREF_TEXT,			LOCAL_PREF, FREF_PREF,		},		'ICN#',		{			LOCAL_APPL, ICON_APPL,			LOCAL_TEXT, ICON_TEXT,			LOCAL_PREF, ICON_PREF,		}	}};///////////////////////////////////////////////////////////////////////	String resources/////////////////////////////////////////////////////////////////////resource 'STR#' ( STRX_Errors, "Error Messages" ){	{		"This program requires color quickdraw."				"  Please load color quickdraw or system 7.0 or later"	}};